mapper.update:
update
    PROD_IMGS
set
    img_url = #{url}
where
    img_no = #{no}

UPDATE PROD_IMGS
SET img_thum = NULL
WHERE color_no = (
    SELECT color_no_sub
    FROM (SELECT color_no AS color_no_sub FROM PROD_IMGS WHERE img_no = #{imgNo}) AS temp_table
);

update PRODUCTS
set
    cat_no = #{category.no}
    , brand_no = #{brand.no}
    , prod_name = #{name}
    , prod_price = #{price}
    , prod_content = #{content}
    , prod_status = #{status}
    , prod_img_thum = #{imgThum}
    , prod_color_num = #{colorNum}
where
    prod_no = #{no}

mapper.select:
select color_no
from PROD_COLORS
where prod_no = #{condition.no}
and color_name = #{condition.color};

mapper.insert:
insert into PRODUCTS
    (
     prod_name
    , prod_price
    , brand_no
    , cat_no
    , prod_content
    , prod_img_thum
    )
values
    (
    #{condition.name}
    , #{condition.price}
    , #{condition.brandNo}
    , #{condition.categoryNo}
    , #{condition.content}
    , #{condition.thumbnail}
    )

mapper.update:
update COURSES
set
  course_no = #{course.no}
, course_name = #{course.name}
, course_time = #{course.time}
, course_distance = #{course.distance}
, course_level = #{course.level}

    , course_filename =  #{course.filename}

,region_no = #{course.region.no}
where
    course_no = #{course.no}

