    select last_insert_id()

mapper.select:
select *, from ( select user_no as no , user_id as id , user_name as name , password , user_nickname as nickname , user_tel as tel , user_email as email , user_created_date as createdDate , user_type as type , provider as provider , ROW_NUMBER() OVER (ORDER BY user_no desc) as rn from USERS
user_name like CONCAT('%',#{condition.value},'%')
user_id like CONCAT('%',#{condition.value},'%')
user_email like CONCAT('%',#{condition.value},'%')
) as subquery where rn between #{condition.begin} and #{condition.end}

select * from ( select L.LESSON_NO as lessonNo , U.USER_NAME as "lecturer.name" , L.LESSON_TITLE as title , L.LESSON_PRICE as price , L.PARTICIPANT as participant , L.LESSON_STATUS as status , L.LESSON_CREATED_DATE as createdDate , L.LESSON_PLAN as plan , L.LESSON_START_DATE as start , L.LESSON_END_DATE as end , L.LESSON_SUBJECT as subject from LESSONS L, USERS U where L.user_ID = U.user_ID
and U.user_name like CONCAT('%',#{condition.value},'%')
and L.lesson_title like CONCAT('%',#{condition.value},'%')
and L.lesson_subject like CONCAT('%',#{condition.value},'%')
and DATE(L.lesson_start_date) = #{condition.day}
) as subquery
