mapper.insert:
insert into REPLIES
(reply_type
, reply_type_no
, reply_content
, user_no
)
values
(
#{reply.type}
, #{reply.typeNo}
, #{reply.content}
, #{reply.user.no}
)

  SELECT LAST_INSERT_ID()

insert into REPLIES
(
prev_reply_no
, reply_type
, reply_type_no
, reply_content
, user_no
)
values
(
#{reply.prevNo}
, #{reply.type}
, #{reply.typeNo}
, #{reply.content}
, #{reply.user.no}
)

mapper.select:
select count(*)
from REPLIES
where reply_type_no = #{no}

mapper.update:
update REPLIES
set prev_reply_no      = #{reply.prevNo}
  , reply_content      = #{reply.content}
  , is_deleted         = #{reply.deleted}
  , reply_updated_date = sysdate()
where reply_no = #{reply.no}

mapper.select:
select EXISTS (SELECT 1
               FROM LIKES
               WHERE like_type_no = #{rno}
                 and like_type = #{type}
                 and user_no = #{userNo})

mapper.delete:
delete
from LIKES
where like_type_no = #{rno}
  and like_type = #{type}
  and user_no = #{userNo}

mapper.update:
update REPLIES
set reply_like_cnt = #{reply.replyLikeCnt}
where reply_no = #{reply.no}
