mapper.update:
update BOARD_REPLIES
set
    prev_reply_no = #{reply.prevNo}
    , reply_content = #{reply.content}
    , is_deleted = #{reply.deleted}
    , reply_updated_date = #{reply.updatedDate}
where
    reply_no = #{reply.no}

mapper.insert:
insert into BOARD_REPLIES
(
board_no
, reply_content
, user_no
)
values
(
#{reply.boardNo}
, #{reply.content}
, #{reply.user.no}
)


