mapper.select:
select R.LESSON_RESERVATION_NO as 'no' , R.LESSON_RESERVATION_STATUS as status , R.USER_ID as "user.id" , R.LESSON_RESERVATION_CREATED_DATE as reservationCreatedDate , R.LESSON_RESERVATION_UPDATED_DATE as reservationUpdatedDate , L.LESSON_NO as "lesson.lessonNo" , L.LESSON_TITLE as "lesson.title" , L.LESSON_PRICE as "lesson.price" , L.LESSON_SUBJECT as "lesson.subject" , L.LESSON_START_DATE as "lesson.start" , L.USER_ID as "user.id" , L.lesson_status as "lesson.status" , date_format(L.lesson_created_date, '%Y-%m-%d') , U.USER_NAME as "lesson.lecturer.name" , P.PAY_ID as "payment.id" from LESSON_RESERVATIONS R INNER JOIN LESSONS L ON R.LESSON_NO = L.LESSON_NO INNER JOIN USERS U ON L.USER_ID = U.USER_ID INNER JOIN PAYMENTS P ON R.LESSON_RESERVATION_PAY_ID = P.PAY_ID where R.USER_ID = #{userId}
AND U.USER_NAME LIKE CONCAT('%', #{condition.searchKeyword}, '%')
AND L.LESSON_TITLE LIKE CONCAT('%', #{condition.searchKeyword}, '%')
AND L.LESSON_SUBJECT LIKE CONCAT('%', #{condition.searchKeyword}, '%')
AND L.LESSON_STATUS = #{condition.lessonStatus}
AND L.LESSON_SUBJECT = #{condition.lessonSubject}
AND R.LESSON_RESERVATION_CREATED_DATE BETWEEN #{condition.start} AND CONCAT(#{condition.end}, ' 23:59:59')

mapper.update:
UPDATE LESSON_RESERVATIONS
SET LESSON_RESERVATION_STATUS = #{status}
WHERE LESSON_RESERVATION_PAY_ID = #{paymentId}
